      function createSkills(){
	    var aboutMe = $('#about-me'), skills = aboutMe.find('#HTML2'), skillsList = skills.find('.widget-content').text().split('|'), frag = $(document.createDocumentFragment());
        for(var i = 1; i < skillsList.length; i++){
		  var li = $('<li/>');
		  var skillName = skillsList[i].split('=')[0];
		  var skillPercentage = skillsList[i].split('=')[1].trim().replace(/\(|\)/gi,'');
  		  li.append('<span class="skill-name">'+skillName.trim()+'</span><div class="bar"><div class="fill"></div><span class="strength">'+skillPercentage+'</span></div>');
		  frag.append(li);
	    }
        var list = $('<ul/>',{'class':'skills-list'}).append(frag);
		skills.find('.widget-content').empty().append(list);
	  }
	  createSkills();
	  /* Resume */
      function createResumeTimeline(){
        var parent = $('#resume'), content = parent.text().trim().split(/\n{2}/), resumeTitle = content[0], resumeDescription = content[1], titleList = resumeTitle.split(/\|\d{1}/g), descriptionList = (resumeDescription != null) ? resumeDescription.split(/\|\d{1}\s\-/g) : '', frag = $(document.createDocumentFragment());
        for(var i = 1; i < titleList.length; i++){
		  var li = $('<li/>'), titleAndYear = titleList[i].split('=')[0], title = titleAndYear.split(' ')[1], year = titleAndYear.split(' ')[2].replace(/\[|\]/g,''), workplaceAndPosition = titleList[i].split('=')[1].split('-'), workplace = workplaceAndPosition[0], position = (workplaceAndPosition[1] != null) ? workplaceAndPosition[1] : '', description = (descriptionList[i] != '') ? descriptionList[i].trim() : '';
		  li.append('<div class="resume-wrapper"><h3><span class="timeline-title">'+title.trim()+'</span><span class="year">'+year.trim()+'</span></h3><p class="workplace-position"><span class="workplace">'+workplace.trim()+'</span><span class="position">'+position+'</span></p><p class="description">'+description+'</p></div>');
		  frag.append(li);
		}
        var list = $('<ul/>',{'class':'resume-list'}).append(frag);
		parent.empty().append(list);
	  }
	  createResumeTimeline();
