      function createContactForm(){
		var contact = $('#contact'), contactForm = contact.find('#ContactForm1'), form = contactForm.find('form'), inputFieldList = form.text().replace(/\*/gi, '').replace(/(\r\n|\r|\n){2,}/g, ' ').trim().split(' ');
        contactForm.find('.contact-form-name').attr('placeholder',inputFieldList[0]+':');
        contactForm.find('.contact-form-email').attr('placeholder',inputFieldList[1]+':');
        contactForm.find('.contact-form-email-message').attr('placeholder',inputFieldList[2]+':');
	  }
	  createContactForm();
