      var changeTitleOnScroll = function(th){
        var currentPosition = $(window).scrollTop();
        $(".kr-section").each(function(){
          var el = $(this), offsetTop = el.offset().top, offsetBottom = el.height() + el.offset().top
          if(currentPosition <= (offsetBottom - th) && currentPosition >= (offsetTop - th)){
            $('#PageList1 li a').removeClass('current');
